/* Создание таблиц для базы данных Деканат ВУЗа */
CREATE TABLE Группы (ID INT PRIMARY KEY, Название VARCHAR(10)) CREATE TABLE Студенты (ID INT PRIMARY KEY, Фамилия VARCHAR(50), ГруппаID INT, FOREIGN KEY (ГруппаID) REFERENCES Группы(ID)) CREATE TABLE Предметы (ID INT PRIMARY KEY, Название VARCHAR(50)) CREATE TABLE Преподаватели (ID INT PRIMARY KEY, Фамилия VARCHAR(50)) CREATE TABLE Сессия (ID INT PRIMARY KEY, СтудентID INT, ПредметID INT, ПреподавательID INT, Оценка INT, Дата DATE, FOREIGN KEY (СтудентID) REFERENCES Студенты(ID), FOREIGN KEY (ПредметID) REFERENCES Предметы(ID), FOREIGN KEY (ПреподавательID) REFERENCES Преподаватели(ID))
